import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Usuario } from './shared';
import { LoginService } from './auth/services/login.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Trabalho Equipe 3';

  constructor(
    private router: Router,
    private loginService: LoginService,

    ) { }
    get usuarioLogado(): Usuario | null {
    return this.loginService.usuarioLogado;
    }
    logout() {
    this.loginService.logout();
    this.router.navigate(['/login']);
    }
}



<div class="conteiner-fluid">
  <nav class="navbar navbar-expand-md n navbar-dark bg-dark">

    <a class="navbar-brand nav-link" href="#" routerLink="/cliente">
    <i class="fa fa-user-edit"></i>{{ title }} </a>


    <div *ngIf="usuarioLogado"
    class="navbar-collapse collapsed justify-content-between"  id="navbarContent">
    <ul class="navbar-nav mr-auto">  
      <li class="nav-item">

      <a class="nav-link" href="#" routerLink="/cliente">Clientes</a>
      </li>


        <li *ngIf="usuarioLogado.perfil==='ADMIN' || usuarioLogado.perfil==='GERENTE'" class="nav-item">
        <a class="nav-link" href="#" routerLink="/produtos">Produtos</a>
        </li>



      <li class="nav-item" ngbDropdown>

        <a class="nav-link" tabindex="0" ngbDropdownToggle id="navbarDropdown1" role="button">  Serviços </a>

        <div ngbDropdownMenu aria-labelledby="navbarDropdown1" class="dropdown-menu">


          <a *ngIf="usuarioLogado.perfil==='ADMIN'|| usuarioLogado.perfil==='FUNC'"
        ngbDropdownItem href="#" routerLink="/pedidos" (click)="$event.preventDefault()">Pedidos</a>



        </div>
      </li>
      </ul>
      <ul *ngIf="usuarioLogado" class="navbar-nav ml-auto">
        <li class="nav-item">
        <a class="nav-link" href="#" (click)="logout()">Logout</a>
        </li>
        <li class="nav-item">
        <p class="navbar-text mb-0 text-white">
        <i class="fa fa-user"></i> {{ usuarioLogado.nome }}
        </p>
        </li>
        </ul>
        </div>
        </nav>
        <router-outlet></router-outlet>
        </div>
